import pandas as pd class GestionCouts:    #db: None    #departement: None        def __init__(self, fichier):        self.db = pd.read_csv(filepath_or_buffer=fichier)        self.departement = tuple(self.db.departement.unique())        self.cout = None            def get_details(self, nom):        employee = self.db.loc[self.db.nom == nom]        # return employee        return pd.DataFrame.from_dict({            "nom": employee["nom"][0],            "absence": employee["absence"][0],            "formation": employee["formation"][0]            }, orient="index")        def obtenir_absence(self, nom):        return self.db.absence[self.db.nom == nom].sum()        def obtenir_formation(self, nom):        return self.db.loc[self.db.nom == nom, "formation"].sum()        def obtenir_cout(self, dept=None):        if dept is None:            return self.db.salaire.sum()        else:            if dept in self.departement:                cout_dept = self.obtenir_cout_dept(dtype="dict")                return cout_dept[dept]            else:                raise ValueError("Le departement n'est pas valide")        def obtenir_cout_dept(self, dtype="dict"):        if dtype == "table":            return self.db[["departement", "salaire"]].groupby("departement").sum().reset_index()        elif dtype == "dict":            cout_dept = {}            for dept in self.db.departement.unique():                cout_dept[dept] = self.db.salaire[self.db.departement == dept].sum()                      return cout_dept        else:            raise TypeError("Veuillez indiquer 'dict' ou 'table'")        if __name__ == "__main__":    # execute only if run as a script    qa= GestionCouts(        fichier="~/learning/programming/python/qa_test/employees.csv"        )        print(qa.obtenir_absence("Ali"))    print(qa.obtenir_formation("Ali"))    print(qa.obtenir_cout_dept(dtype="table"))    print(f"Le cout de l'annee 2021 est : {qa.obtenir_cout(dept='vente')}")    print(qa.obtenir_cout())    print(qa.get_details("Ali"))